    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        Juego j = new Juego();
        String jugador_1 = nombre_jugador1.getText();
        String jugador_2 = nombre_jugador_2.getText();
        String idioma =this.ideoma.getSelectedItem().toString();
        if (jugador_1 != null && jugador_2 != null && !"".equals(jugador_1) && !"".equals(jugador_2)) {
            j.setJugador1(jugador_1);
            j.setJugador2(jugador_2);
            j.setIdioma(FactoryMethodIdioma.creaIdeoma(idioma));
            j.iniciarJuego();
            this.setVisible(false);
        }else{
            JOptionPane.showMessageDialog(this, "Ingresa los nombres de los jugadores", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

